import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [url, setUrl] = useState('');
  const [questions, setQuestions] = useState([]);
  const [category, setCategory] = useState('');
  const [loading, setLoading] = useState(false);
  const [submitType, setSubmitType] = useState(''); // Track which button was clicked

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setQuestions([]);
    setCategory('');

    try {
      let response;

      if (submitType === 'openai') {
        // Call the backend endpoint to submit to OpenAI
        response = await axios.post('http://localhost:5000/submit-to-openai', { url });
        const questionsData = response.data.questions;
        setQuestions(questionsData);
      } else if (submitType === 'ollama') {
        // Call the backend endpoint to submit to Ollama
        response = await axios.post('http://localhost:5000/generate-questions', { url });
        const analysisData = response.data.analysis;
        setCategory(analysisData);
      }
    } catch (error) {
      console.error("Error with request:", error);
      alert("Failed to process request. Please try again.");
    } finally {
      setLoading(false);
      setSubmitType(''); // Reset submitType after the request
    }
  };

  const handleCategorizeSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:5000/categorize', { answers });
      setCategory(response.data.category);
    } catch (error) {
      console.error("Error categorizing:", error);
      alert("Failed to categorize. Please try again.");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full text-center">
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Visitor Web Scraper</h1>

        {/* Form with two submit buttons */}
        <form onSubmit={handleUrlSubmit} className="mb-6">
          <label className="block text-gray-600 mb-2">
            Enter URL You Wish to Scrap:
            <input
              type="text"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              required
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            />
          </label>

          {/* Submit to OpenAI Button */}
          <button
            onClick={() => setSubmitType('openai')}
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2 hover:bg-blue-600 transition-colors duration-200"
          >
            Submit to OpenAI
          </button>

          {/* Submit to Ollama Button */}
          <button
            onClick={() => setSubmitType('ollama')}
            type="submit"
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors duration-200"
          >
            Submit to Ollama
          </button>
        </form>

        {loading && <p className="text-gray-500">Loading...</p>}

        {questions.length > 0 && (
          <div className="text-left">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Questions</h2>
            {questions.map((question, index) => (
              <div key={index} className="mb-4">
                <p className="text-gray-700">{question}</p>
              </div>
            ))}

            {/* Categorize Button */}
            <button
              onClick={handleCategorizeSubmit}
              className="bg-orange-500 text-white px-4 py-2 rounded-md hover:bg-orange-600 transition-colors duration-200"
            >
              Categorize
            </button>
          </div>
        )}

        {category && (
          <div className="mt-4">
            <h2 className="text-xl font-semibold text-gray-800 mb-2">Category</h2>
            <p className="text-gray-700">{category}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
