import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [url, setUrl] = useState('');
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [category, setCategory] = useState('');
  const [loading, setLoading] = useState(false);

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setQuestions([]);
    setCategory('');

    try {
      const response = await axios.post('http://localhost:5000/generate-questions', { url });
      const questionsData = JSON.parse(response.data.questions).questions;
      setQuestions(questionsData);
    } catch (error) {
      console.error("Error fetching questions:", error);
      alert("Failed to retrieve questions. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleOptionChange = (questionIndex, answer) => {
    setAnswers(prevAnswers => ({
      ...prevAnswers,
      [questionIndex]: answer
    }));
  };

  const handleCategorizeSubmit = async () => {
    const userResponse = Object.values(answers).join(", ");

    try {
      const response = await axios.post('http://localhost:5000/categorize', { user_response: userResponse });
      setCategory(response.data.category);
    } catch (error) {
      console.error("Error categorizing user:", error);
      alert("Failed to categorize. Please try again.");
    }
  };

  return (
    <div className="App">
      <h1>Webpage Information</h1>
      <form onSubmit={handleUrlSubmit}>
        <label>
          Enter URL:
          <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} required />
        </label>
        <button type="submit">Submit</button>
      </form>

      {loading && <p>Loading questions...</p>}

      {questions.length > 0 && (
        <div>
          <h2>Questions</h2>
          {questions.map((question, index) => (
            <div key={index}>
              <p>{question.question}</p>
              {question.options.map((option, i) => (
                <label key={i}>
                  <input
                    type="radio"
                    name={`question-${index}`}
                    value={option}
                    onChange={() => handleOptionChange(index, option)}
                  />
                  {option}
                </label>
              ))}
            </div>
          ))}
          <button onClick={handleCategorizeSubmit}>Submit Answers</button>
        </div>
      )}

      {category && (
        <div>
          <h2>Your Category</h2>
          <p>{category}</p>
        </div>
      )}
    </div>
  );
}

export default App;
